
-- ─────────────────────────────────────────────────────────────────────────────
-- Extensions
-- ─────────────────────────────────────────────────────────────────────────────
CREATE EXTENSION IF NOT EXISTS pgcrypto;  -- gen_random_uuid()

-- ─────────────────────────────────────────────────────────────────────────────
-- Enums
-- ─────────────────────────────────────────────────────────────────────────────
DO $$ BEGIN
  CREATE TYPE user_role AS ENUM ('technician','staff','admin');
EXCEPTION WHEN duplicate_object THEN NULL; END $$;

DO $$ BEGIN
  CREATE TYPE signature_source AS ENUM ('drawn','saved','applied');
EXCEPTION WHEN duplicate_object THEN NULL; END $$;

DO $$ BEGIN
  CREATE TYPE odometer_source AS ENUM ('obd','gps','manual','unknown');
EXCEPTION WHEN duplicate_object THEN NULL; END $$;

-- ─────────────────────────────────────────────────────────────────────────────
-- Updated-at helper
-- ─────────────────────────────────────────────────────────────────────────────
CREATE OR REPLACE FUNCTION set_updated_at()
RETURNS trigger AS $$
BEGIN
  NEW.updated_at := now();
  RETURN NEW;
END
$$ LANGUAGE plpgsql;

-- ─────────────────────────────────────────────────────────────────────────────
-- app_users: all employees (some are technicians)
-- ─────────────────────────────────────────────────────────────────────────────
CREATE TABLE IF NOT EXISTS app_users (
  id            uuid PRIMARY KEY DEFAULT gen_random_uuid(),
  email         text UNIQUE,
  full_name     text NOT NULL,
  role          user_role NOT NULL DEFAULT 'staff',
  is_active     boolean NOT NULL DEFAULT true,
  created_at    timestamptz NOT NULL DEFAULT now(),
  updated_at    timestamptz NOT NULL DEFAULT now(),
  password_phc  text
);

CREATE INDEX IF NOT EXISTS idx_app_users_full_name ON app_users (full_name);
CREATE INDEX IF NOT EXISTS idx_app_users_active ON app_users (is_active);

DROP TRIGGER IF EXISTS trg_app_users_updated_at ON app_users;
CREATE TRIGGER trg_app_users_updated_at
BEFORE UPDATE ON app_users
FOR EACH ROW EXECUTE FUNCTION set_updated_at();

COMMENT ON TABLE app_users IS 'All employees. Some are technicians (see technicians table).';

-- ─────────────────────────────────────────────────────────────────────────────
-- technicians: only those who perform inspections (1:1 with app_users)
-- ─────────────────────────────────────────────────────────────────────────────
CREATE TABLE IF NOT EXISTS technicians (
  id                         uuid PRIMARY KEY DEFAULT gen_random_uuid(),
  app_user_id                uuid NOT NULL UNIQUE REFERENCES app_users(id) ON DELETE CASCADE,

  sto_registration_number    text,
  trade_codes                text[] NOT NULL DEFAULT '{}',

  -- Saved signature for "Click to Declare & Sign"
  signature_image            bytea,
  signature_image_mime       text DEFAULT 'image/png'
                               CHECK (signature_image_mime IN ('image/png','image/svg+xml','image/jpeg')),
  signature_last_updated     timestamptz,

  is_active                  boolean NOT NULL DEFAULT true,
  created_at                 timestamptz NOT NULL DEFAULT now(),
  updated_at                 timestamptz NOT NULL DEFAULT now(),

  CONSTRAINT technicians_signature_size_ok
    CHECK (signature_image IS NULL OR octet_length(signature_image) <= 1572864) -- 1.5MB
);

-- Ensure STO # is unique when present (allows multiple NULLs)
CREATE UNIQUE INDEX IF NOT EXISTS uniq_technicians_sto
  ON technicians (sto_registration_number)
  WHERE sto_registration_number IS NOT NULL;

CREATE INDEX IF NOT EXISTS idx_technicians_active ON technicians (is_active);

DROP TRIGGER IF EXISTS trg_technicians_updated_at ON technicians;
CREATE TRIGGER trg_technicians_updated_at
BEFORE UPDATE ON technicians
FOR EACH ROW EXECUTE FUNCTION set_updated_at();

COMMENT ON TABLE technicians IS 'Technician-only profile data: STO #, Trade Codes, saved signature.';
COMMENT ON COLUMN technicians.trade_codes IS 'Array of Trade Code(s) for the technician.';

-- ─────────────────────────────────────────────────────────────────────────────
-- schedule4_inspections: one row per submitted inspection
-- References technicians; stores a frozen snapshot of the signature used
-- ─────────────────────────────────────────────────────────────────────────────
CREATE TABLE IF NOT EXISTS schedule4_inspections (
  id                               uuid PRIMARY KEY DEFAULT gen_random_uuid(),

  -- who did it (both for audit and denormalization)
  technician_id                    uuid REFERENCES technicians(id) ON DELETE SET NULL,
  app_user_id                      uuid REFERENCES app_users(id)   ON DELETE SET NULL,

  -- frozen identity at sign time (even if profiles change later)
  technician_name                  text NOT NULL,
  technician_sto_registration_num  text,
  technician_trade_codes           text[] NOT NULL DEFAULT '{}',

  -- signature snapshot used on THIS inspection
  technician_signature_image       bytea,
  technician_signature_image_mime  text DEFAULT 'image/png'
                                     CHECK (technician_signature_image_mime IN ('image/png','image/svg+xml','image/jpeg')),
  signature_source                 signature_source NOT NULL DEFAULT 'drawn',

  -- when
  performed_at                     timestamptz NOT NULL DEFAULT now(),

  -- vehicle snapshot (from UI/Samsara)
  vehicle_samsara_id               text,
  vehicle_name                     text,
  license_plate                    text,

  -- odometer & lifecycle
  odometer_km                      integer,
  odometer_source                  odometer_source NOT NULL DEFAULT 'unknown',
  expiry_date                      date,
  next_service_odometer_km         integer,

  -- misc
  location                         text,
  notes                            text,

  -- full Schedule 4 payload (lossless)
  payload_json                     jsonb NOT NULL,

  created_at                       timestamptz NOT NULL DEFAULT now(),
  updated_at                       timestamptz NOT NULL DEFAULT now(),

  CONSTRAINT schedule4_signature_size_ok
    CHECK (technician_signature_image IS NULL OR octet_length(technician_signature_image) <= 1572864)
);

CREATE INDEX IF NOT EXISTS idx_sched4_created_at   ON schedule4_inspections (created_at);
CREATE INDEX IF NOT EXISTS idx_sched4_performed_at ON schedule4_inspections (performed_at);
CREATE INDEX IF NOT EXISTS idx_sched4_technician   ON schedule4_inspections (technician_id);
CREATE INDEX IF NOT EXISTS idx_sched4_app_user     ON schedule4_inspections (app_user_id);
CREATE INDEX IF NOT EXISTS idx_sched4_vehicle_lp   ON schedule4_inspections (license_plate);
CREATE INDEX IF NOT EXISTS idx_sched4_payload_gin  ON schedule4_inspections USING GIN (payload_json jsonb_path_ops);

DROP TRIGGER IF EXISTS trg_sched4_updated_at ON schedule4_inspections;
CREATE TRIGGER trg_sched4_updated_at
BEFORE UPDATE ON schedule4_inspections
FOR EACH ROW EXECUTE FUNCTION set_updated_at();

COMMENT ON TABLE schedule4_inspections IS 'Schedule 4 submissions; immutable snapshot of signature & form payload.';
COMMENT ON COLUMN schedule4_inspections.signature_source IS 'drawn (canvas), saved (from profile), or applied (Click to Declare & Sign).';


